def normalize_text(text):
    text = unicodedata.normalize('NFKD', text).encode('ascii', 'ignore').decode('ascii')
    return text.lower()

@app.route('/', methods=['GET', 'POST'])
def index():
    if request.method == 'POST':
        user_input = normalize_text(request.form['message'].strip())
        print(f"Normalized User Input: {user_input}")  # Debugging line
        
        # Crear la respuesta del modelo
        chat_session.history.append({
            "role": "user",
            "parts": [user_input]
        })
        response = chat_session.send_message(user_input)
        chat_session.history.append({
            "role": "model",
            "parts": [response.text]
        })
        
        response_text = response.text
        return jsonify({'respuesta': response_text, 'follow_up': []})
    return render_template('index.html', titulo="Asistente Virtual")

@app.route('/reset', methods=['POST'])
def reset():
    chat_session.history = chat_session.history[:4]  # Reseteamos al estado inicial del chat
    return jsonify({'status': 'reset'})

if __name__ == '__main__':
    app.run(debug=True)
